mapper.select:

	select no, name, email 
		from member 
	where email= #{email} and password= #{password}



	select no, name, email, password, gender
		from member
	where no= #{no}



	select no, name, email, password, gender
		from member
	where email= #{email}

mapper.update:


	update member 
		set name=#{name}, email=#{email}, password=#{password}, gender=#{gender} 
	where no=#{no}
